function toggleDarkMode() {
  document.getElementById("color-changer-body").classList.toggle("dark-mode");
}

// Function to change background color and store it in localStorage
function changeBackgroundColor(color) {
  localStorage.setItem("selectedColor", color); // Store color in localStorage
  applyBackgroundColor(color); // Apply immediately
}

// Function to apply background color safely (checks if elements exist)
function applyBackgroundColor(color) {
  const elements = [
    "nav-second",
    "nav-dropdown-color",
    "explore-courses-cta",
    "prepcampusplus-text",
    "mock-registerbtn",
    "footer",
    "go-back-to-home",
    "overview-header"
  ];

  elements.forEach(id => {
    let element = document.getElementById(id);
    if (element) {
      if (id === "prepcampusplus-text") {
        element.style.color = color; // Text color for this element
      } else {
        element.style.backgroundColor = color;
      }
    }
  });
}

// Apply stored background color when the page loads
function applyStoredBackgroundColor() {
  let savedColor = localStorage.getItem("selectedColor");
  if (savedColor) {
    applyBackgroundColor(savedColor);
  }
}

// Run this function when the page loads
document.addEventListener("DOMContentLoaded", applyStoredBackgroundColor);
