function toggleDarkMode(){document.getElementById("color-changer-body").classList.toggle("dark-mode")}
function changeBackgroundColor(e){localStorage.setItem("selectedColor",e),applyBackgroundColor(e)}
function applyBackgroundColor(e){["nav-second","nav-dropdown-color","explore-courses-cta","prepcampusplus-text","mock-registerbtn","footer","go-back-to-home","overview-header"].forEach(t=>{let o=document.getElementById(t);o&&("prepcampusplus-text"===t?o.style.color=e:o.style.backgroundColor=e)})}
function applyStoredBackgroundColor(){let e=localStorage.getItem("selectedColor");e&&applyBackgroundColor(e)}
document.addEventListener("DOMContentLoaded",applyStoredBackgroundColor);
