document.addEventListener("DOMContentLoaded", function () {
  // When all content, images, and scripts are fully loaded
  window.onload = function () {
    let loader = document.getElementById("preloader");
    if (loader) {
      loader.style.opacity = "0"; // Smooth fade out
      setTimeout(() => loader.style.display = "none", 500); // Hide after transition
    }
  };
});
document.addEventListener("DOMContentLoaded", function () {
  window.onload = function () {
    let loader = document.getElementById("preloader");
    let mainContent = document.getElementById("main-wrapper");

    if (loader) {
      loader.style.opacity = "0"; // Fade out loader
      setTimeout(() => {
        loader.style.display = "none"; // Hide loader
        if (mainContent) {
          mainContent.style.opacity = "1"; // Fade in content smoothly
        }
      }, 500);
    }
  };
});
